import React from 'react';
import { Container, Col, Card } from 'react-bootstrap';
import { connect } from 'react-redux';
import Notes from './Notes';
import NewNotes from './NewNotes';

const Landing = ({ isLoggedIn, isEditClicked, loggedInUser }) => {

  let landing;

  if(isLoggedIn && !isEditClicked){
    //NewNotes form is hidden in the landing page if user is editing the notes
    landing = <div>
                <NewNotes/> 
                <Notes/>
              </div>
  }else{
    landing = <Notes/>
  }

    return (
      <Container>
        {loggedInUser.map((user, i) => (
                    <Col key={i} className='mb-3 col-sm-12'>
                        <Card>
                            <div style={{margin: '4px 4px 4px 4px'}}>
                              <p style={{fontSize:'12px', display:'inline-block'}}> {user}</p>
                            </div>
                        </Card>
                    </Col>
                ))}
        {landing}
      </Container>
    )
}

const mapStateToProps = state => ({
  isLoggedIn: state.loginReducer.isLoggedIn,
  loggedInUser: state.loginReducer.loggedInUser,
  isEditClicked: state.notesReducer.isEditClicked,
})

export default connect(mapStateToProps)(Landing);
