import React, {useEffect} from 'react';
import { connect } from 'react-redux';
import ScrollToBottom from 'react-scroll-to-bottom';
import Topic from './Topic';
import { getAllTopic } from '../redux/actions/topicActions';


const ShowTopic = ({dispatch, topic, allTopic}) => {

    useEffect(() => {
        dispatch(getAllTopic());
    });

    return(
        <div>
            <ScrollToBottom key={i}>
                {allTopic.map((subject, i) => (    
                    <Topic topic={subject.topic}/>
                ))}
            </ScrollToBottom>
        </div>
    )
}

const mapStateToProps = state => ({
    topic: state.topicReducer.topic,
    allTopic: state.topicReducer.allTopic,
})

export default connect(mapStateToProps)(ShowTopic);
