import React from 'react';

const Message = ({ senderName, message, username }) => {

    let content;

    if(senderName === username) {
        content = <div style={{justifyContent: 'flex-start'}}>
                    <div>
                        <h6>{senderName}</h6>
                    </div>
                        <p>{message}</p>
                  </div>
    }else{
        content = <div style={{justifyContent: 'flex-end'}}>
                    <div>
                        <h6>{senderName}</h6>
                    </div>
                        <p>{message}</p>
                  </div>
    }


    return(
        <div>
            {content}
        </div>
    )
}

const mapStateToProps = state => ({
    username: state.loginReducer.username,
});

export default Message;
