import React from 'react';

const divStyle = {
    color: 'red'
  };



const Message = ({ senderName, message, username }) => {

    let content;

    if(senderName === username) {
        content = <div>
                        <p style={divStyle}>{senderName}</p>
                  </div>
    }else{
        content = <div style={{justifyContent : 'flex-end'}}>

                        <p>{senderName}</p>
                        <p>{message}</p>
                  </div>
    }


    return(
        <div>
            {content}
        </div>
    )
}

const mapStateToProps = state => ({
    username: state.loginReducer.username,
});

export default Message;
