import React, {useEffect} from 'react';
import { connect } from 'react-redux';
import { setAllTopic, getAllTopic } from '../redux/actions/topicActions';

const ShowTopic = ({dispatch, topic, allTopic}) => {

    useEffect((e) => {
        e.preventDefault();
        dispatch(getAllTopic()).then((res) => {
            console.log('showTopic component')
        })
    }, [allTopic]);

    return(
        <div>
            {allTopic.map((subject, i) => (    
            <div key={i}>
                <p>{subject}</p>
            </div>
            ))}
        </div>
    )
}

const mapStateToProps = state => ({
    topic: state.topicReducer.topic,
    allTopic: state.topicReducer.allTopic,
})

export default connect(mapStateToProps)(ShowTopic);
