import React, { useEffect } from 'react';
import { connect } from 'react-redux';
import { Form, Button } from 'react-bootstrap';
import { setMessage } from '../redux/actions/topicActions';

const Input = ({ dispatch, message, sendMessage }) => {

    useEffect(()=>{
        console.log(message)
    })

    return(
        <Form>
            <Form.Group>
                <Form.Control 
                    type='text'
                    placeholder= 'Type a message...'
                    value={message}
                    onChange ={({target: {value}}) => dispatch(setMessage(value))}
                />
            </Form.Group>
            <Button variant='primary' type='submit' onClick={e => sendMessage(e)}>
                Send
            </Button>
        </Form> 
    )
}

const mapStateToProps = state => ({
    message: state.topicReducer.message,
});

export default connect(mapStateToProps)(Input);
