import React from 'react';
import { connect } from 'react-redux';
import { Form, Button } from 'react-bootstrap';
import { setContent, addNotes, setIsNoteAdded } from '../redux/actions/notesActions';

const Input = ({dispatch, isLoggedIn}) => {

    const userVerification = (e) => {
        e.preventDefault();
        if(isLoggedIn) {
            dispatch(addNotes()).then(res => {
                if(res === true){
                    dispatch(setIsNoteAdded(true)); //set isNoteAdded to true after each submission
                }
            }); 
        }
    }

    return (
        <Form>
            <Form.Group>
                <Form.Control 
                    type='text'
                    placeholder='Type a message...'
                    onChange={e => dispatch(setContent(e.target.value))}
                />
            </Form.Group>
            <Button variant='primary' type='submit' onClick={userVerification}>
                Send
            </Button>
        </Form> 
    )
}

const mapStateToProps = state => ({
    content: state.notesReducer.content,
    isNoteAdded: state.notesReducer.isNoteAdded,
    isLoggedIn: state.loginReducer.isLoggedIn
});

export default connect(mapStateToProps)(Input);
