export const setTopic = topic => ({ 
    type: 'SET_TOPIC',
    topic,
  });

export const addNewTopic = () => (dispatch, getState) => {
    const { topic } = getState().topicReducer;
    const { username } = getState().loginReducer;
    console.log(topic);
    console.log(username);
}
