import React, { useState, useEffect } from "react";
import { connect } from 'react-redux';
import { Button, Form } from "react-bootstrap";
import { setTopic, addNewTopic } from '../redux/actions/topicActions';

const NewTopic = ({ dispatch }) => { 

  // const onClick = (e) => {
  //   e.preventDefault();
  //   dispatch(addNewTopic()).then(res => {
  //     console.log(res)
  //   });
  // }

  return (
    <Form>
        <Form.Group>
            <Form.Control 
                type='text'
                placeholder= 'Create a new topic...'
                onChange = {e => dispatch(setTopic(e.target.value))}
            />
        </Form.Group>
        <Button variant='primary' type='submit'
          // onClick={onClick}
        >
            +
        </Button>
    </Form> 
  );
}

const mapStateToProps = state => ({
  topic: state.topicReducer.topic
});

export default connect(mapStateToProps)(NewTopic);
