import React, {useEffect} from 'react';

const ShowTopic = ({topic}) => {
    return(
        <div>
            {topic.map((subject, i) => (    
            <div key={i}>
                <p>{subject}</p>
            </div>
            ))}
        </div>
    )
}

const mapStateToProps = state => ({
    topic: state.topicReducer.topic,
})

export default connect(mapStateToProps)(ShowTopic);
